transform.tRule:
::NFC;

$wordBoundary = [^[:L:][:M:][:N:]];
$upperVowels  = [АЕЁЭИОУЫЮЯ];
$lowerVowels  = [аеёэиоуыюя];
$vowels       = [$upperVowels $lowerVowels];
$upper        = [:Uppercase:];
$lower        = [:Lowercase:];
$ZSTC         = [ЗзСсТтЦц];
$CHSH         = [ЧчШшЩщЖж];
$IJ           = [ИиЙй];
$Front        = [ЕеИиЫыЭэ];
$FrontUpper   = [ЕИЫЭ];
$FrontLower   = [еиыэ];

# --- Г: gu before e/i/ы; else g ---
Г } $FrontLower → Gu;
Г } $FrontUpper → GU;
г } $FrontLower → gu;
г } $FrontUpper → gu;

# --- Е (E) ---
[$upper-$upperVowels] { Е } → E;
[$lower-$lowerVowels] { е } → e;
$IJ { Е } → E;
$IJ { е } → e;
Ь { Е } $lower → ie;
Ь { Е } [^$lower] → IE;
ь { е } → ie;
Ъ { Е } $lower → ie;
Ъ { Е } [^$lower] → IE;
ъ { е } → ie;
$wordBoundary { Е } $lower → Ie;
$wordBoundary { Е } [^$lower] → IE;
$wordBoundary { е } → ie;
[$vowels-[Ии]] { Е } $lower → ïe;
[$vowels-[Ии]] { Е } [^$lower] → ÏE;
[$vowels-[Ии]] { е } → ïe;

# --- Ё (YO) ---
# convention admitted first (specific) → e
$CHSH { Ё } → e;
$CHSH { ё } → e;
# default Io/io with right-case
Ё } $lower → io;
Ё } [^$lower] → IO;
ё → io;

# --- И (I) ---
[$vowels-[Ии]] { И } → Ï;
[$vowels-[Ии]] { и } → ï;

# --- Й (JOT) ---
ий $ → i;
Ий $ → i;
ый $ → y;
Ый $ → y;
[ИиЬь] { Й } [Ее] → ;
Й → Ï;
й → ï;

# --- Ю (YU) ---
$IJ { Ю } $lower → ou;
$IJ { Ю } [^$lower] → OU;
$IJ { ю } → ou;
[ЕЭИЯЮеэияю] { Ю } $lower → ïou;
[ЕЭИЯЮеэияю] { Ю } [^$lower] → ÏOU;
[ЕЭИЯЮеэияю] { ю } → ïou;
[АОУЫаоуы] { Ю } $lower → you;
[АОУЫаоуы] { Ю } [^$lower] → YOU;
[АОУЫаоуы] { ю } → you;
Ю } $lower → iou;
Ю } [^$lower] → IOU;
ю → iou;

# --- Я (YA) ---
$IJ { Я } $lower → a;
$IJ { Я } [^$lower] → A;
$IJ { я } → a;
[$vowels-$IJ] { Я } $lower → ïa;
[$vowels-$IJ] { Я } [^$lower] → ÏA;
[$vowels-$IJ] { я } → ïa;
$wordBoundary { Я } $lower → Ya;
$wordBoundary { Я } [^$lower] → YA;
$wordBoundary { я } → ya;
Я } $lower → ia;
Я } [^$lower] → IA;
я → ia;

# --- С between vowels → ss ---
$vowels { С } $vowels → ss;
$vowels { с } $vowels → ss;

# --- Н final after и/ы → ne (lowercase only) ---
[иы] { н } $ → ne;
[иы] { н } $wordBoundary → ne;

# --- Final -ев → -iev ---
ЕВ $ → iev;
ев $ → iev;

# --- Simple letters and digraphs ---
А → A;
а → a;
Б → B;
б → b;
В → V;
в → v;
Г → G;
г → g;
Д → D;
д → d;
Е → E;
е → e;
Ж → J;
ж → j;
З → Z;
з → z;
И → I;
и → i;
К → K;
к → k;
Л → L;
л → l;
М → M;
м → m;
Н → N;
н → n;
О → O;
о → o;
П → P;
п → p;
Р → R;
р → r;
С → S;
с → s;
Т → T;
т → t;

У } $lower → ou;
У } [^$lower] → OU;
у → ou;

Х } $lower → kh;
Х } [^$lower] → KH;
х → kh;

Ц } $lower → ts;
Ц } [^$lower] → TS;
ц → ts;

Ч } $lower → tch;
Ч } [^$lower] → TCH;
ч → tch;

Ш } $lower → ch;
Ш } [^$lower] → CH;
ш → ch;

Щ } $lower → chtch;
Щ } [^$lower] → CHTCH;
щ → chtch;

Ъ → ;
ъ → ;
Ы → y;
ы → y;
Ь → ;
ь → ;
Э } $lower → e;
Э } [^$lower] → E;
э → e;

::NFC;
